import { ExportTransaction, ImportTransaction, JVMExportTransaction, JVMImportTransaction } from '../../../../src'

describe('JVMExportTransaction', () => {
  test.each([
    {
      description: 'Parsing from JVMAPI hex data',
      data: ''
    }
  ])('$description', async ({ data }) => {
    const parsed = JVMExportTransaction.parse(data)
    expect(parsed).toBeInstanceOf(ExportTransaction)
  })
})

describe('JVMImportTransaction', () => {
  test.each([
    {
      description: 'Parsing from JVMAPI hex data',
      data: ''
    }
  ])('$description', async ({ data }) => {
    const parsed = JVMImportTransaction.parse(data)
    expect(parsed).toBeInstanceOf(ImportTransaction)
  })
})
